import React, { Component } from 'react';
import './App.css';

// ================================= Clock Component ================================= //
// Using Class Ex
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {date: new Date()}
  }

  render() {
    return (
      <section className="App">
        <h1>Time: {this.state.date.toLocaleTimeString()}</h1>
      </section>
    )
  }
}

export default App;

// ================================= Welcome Component ================================= //
class Welcome extends Component {
  render() {
    return <h2>Hello, {this.props.name}</h2>
  }
}

const element = <Welcome name="Israel Torres" />;

// Using Const Ex
export const WelcomeComp = props => {         
    return (
      <section className="App">
        <Welcome name={'Israel Torres'} />
      </section>
    ) 
} 

// ================================= MyProps Component ================================= //
// Using Export Ex
export class MyProps extends Component { 
  render() {          
     return (
      <section className="App">
        <h4>Class name: {this.props.propsClassName}</h4>
        <h4>Assigment: {this.props.propsAssignmentName}</h4>
      </section>
     ) 
  }
} 

//Props
MyProps.defaultProps = {
  propsClassName: "Programming for Web Applications",
  propsAssignmentName: "React.js Install"
}
